<template>
  <div class="profile-card">
    <div class="avatar" @click="onUserClick">
      <img v-if="profile.avatar" :src="profile.avatar" alt="avatar" />
      <div v-else class="avatar-fallback">{{ initials }}</div>
    </div>
    <div class="info" @click="onUserClick">
      <div class="nickname">{{ profile.nickname }}</div>
      <div class="meta" v-if="profile.meta">{{ profile.meta }}</div>
    </div>
    <div class="more-options-wrapper" ref="dropdownRef">
      <button class="more-button" @click="toggleMenu">
        <svg class="more-icon" width="6" height="30" viewBox="0 0 6 30" fill="none" xmlns="http://www.w3.org/2000/svg">
            <circle cx="3" cy="6" r="2" fill="currentColor"/>
            <circle cx="3" cy="15" r="2" fill="currentColor"/>
            <circle cx="3" cy="24" r="2" fill="currentColor"/>
        </svg>
      </button>
      
      <!-- Dropdown Menu -->
      <Transition name="dropdown-fade">
        <div v-if="showMenu" class="options-dropdown" @click.stop>
          <button class="dropdown-item" @click="handleCopyProfileLink">
            <svg width="256" height="256" viewBox="0 0 256 256" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                <rect width="256" height="256" fill="url(#pattern0_41_2)"/>
                <defs>
                <pattern id="pattern0_41_2" patternContentUnits="objectBoundingBox" width="1" height="1">
                <use xlink:href="#image0_41_2" transform="scale(0.00390625)"/>
                </pattern>
                <image id="image0_41_2" width="256" height="256" preserveAspectRatio="none" xlink:href="data:image/png;base64,"/>
                </defs>   
            </svg>
            <span>Copy link</span>
          </button>
          <button class="dropdown-item" @click="handleCopyUserId">
            <svg width="256" height="256" viewBox="0 0 256 256" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                <rect width="256" height="256" fill="url(#pattern0_41_3)"/>
                <defs>
                <pattern id="pattern0_41_3" patternContentUnits="objectBoundingBox" width="1" height="1">
                <use xlink:href="#image0_41_3" transform="scale(0.00390625)"/>
                </pattern>
                <image id="image0_41_3" width="256" height="256" preserveAspectRatio="none" xlink:href="data:image/png;base64,"/>
                </defs>
            </svg>
            <span>Copy ID</span>
          </button>
        </div>
      </Transition>
    </div>
  </div>
</template>

<script setup lang="ts">
import { computed, ref, onMounted, onUnmounted } from 'vue'

interface AdminProfileCardProps {
  profile: { id: number; nickname: string; avatar?: string; meta?: string }
}
const props = defineProps<AdminProfileCardProps>()
const showMenu = ref(false)
const dropdownRef = ref<HTMLElement | null>(null)

const initials = computed(() => {
  const n = props.profile.nickname || ''
  return n.slice(0, 1).toUpperCase()
})

const emit = defineEmits<{
  (e: 'userClick', userId: number): void
}>()

function onUserClick() {
  emit('userClick', props.profile.id)
}

function toggleMenu() { showMenu.value = !showMenu.value }
function onOpenProfile() { showMenu.value = false }
function onReport() { showMenu.value = false }

function handleCopyProfileLink() {
  // TODO: Implement copy profile link functionality
  showMenu.value = false
}

function handleCopyUserId() {
  // TODO: Implement copy user ID functionality
  showMenu.value = false
}

function handleClickOutside(event: Event) {
  const target = event.target as HTMLElement
  
  if (dropdownRef.value && !dropdownRef.value.contains(target)) {
    showMenu.value = false
  }
}

onMounted(() => {
  document.addEventListener('click', handleClickOutside)
})

onUnmounted(() => {
  document.removeEventListener('click', handleClickOutside)
})
</script>

